mapper.delete:
DELETE
FROM LIKEINFO
WHERE USERSEQ = #{userSeq}
AND POSTSEQ = #{postSeq}

mapper.select:
SELECT IF(COUNT(*) = 1, 1, 0)
FROM LIKEINFO
WHERE USERSEQ = #{userSeq}
  AND POSTSEQ = #{postSeq}

mapper.delete:
DELETE
FROM LIKEINFO
WHERE USERSEQ = #{userSeq}
  AND COMMENTSEQ = #{commentSeq}

mapper.select:
SELECT IF(COUNT(*) = 1, 1, 0)
FROM LIKEINFO
WHERE USERSEQ = #{userSeq}
  AND COMMENTSEQ = #{commentSeq}
